function y = doFilter(x)
%DOFILTER Filters input x and returns output y.

% MATLAB Code
% Generated by MATLAB(R) 9.1 and the DSP System Toolbox 9.3.
% Generated on: 11-Jan-2017 13:55:04

persistent Hd;

if isempty(Hd)
    
    N     = 3;    % Order
    Fpass = 40;   % Passband Frequency
    Fstop = 49;   % Stopband Frequency
    Fs    = 100;  % Sampling Frequency
    
    h = fdesign.lowpass('n,fp,fst', N, Fpass, Fstop, Fs);
    
    Hd = design(h, 'equiripple', ...
        'StopbandShape', 'flat');
    
    
    
    set(Hd,'PersistentMemory',true);
    
end

y = filter(Hd,x);

