function y = doFilter(x)
%DOFILTER Filters input x and returns output y.

% MATLAB Code
% Generated by MATLAB(R) 9.0 and the DSP System Toolbox 9.2.
% Generated on: 15-Jan-2017 20:52:56

persistent Hd;

if isempty(Hd)
    
    N     = 4;   % Order
    Fpass = 0.01;   % Passband Frequency
    Fstop = 25;   % Stopband Frequency
    Apass = 1;    % Passband Ripple (dB)
    Fs    = 100;  % Sampling Frequency
    
    h = fdesign.lowpass('n,fp,fst,ap', N, Fpass, Fstop, Apass, Fs);
    
    Hd = design(h, 'equiripple');
    
    
    
    set(Hd,'PersistentMemory',true);
    
end

y = filter(Hd,x);

